enum layers {
    _BASE,
    _NUMBERS,
    _PAGECTRL,
    _SYMBOLS,
    _SYSCTRL,
};

enum custom_keycodes {
    SCRN_BR_UP = SAFE_RANGE,
    SCRN_BR_DN,
    L_RGB_NXT,                                       
    L_RGB_BR_UP,
    L_RGB_BR_DN,
    L_RGB_TGGL
};

// #define LT_NUMBERS LT(_NUMBERS, KC_SPC)
// #define LT_PAGECTRL LT(_PAGECTRL, KC_BSPC)
// #define LT_SYMBOLS LT(_SYMBOLS, KC_ENT)

#define LT_SYMBOLS LT(_SYMBOLS, KC_SPC)
#define LT_PAGECTRL LT(_PAGECTRL, KC_DEL)
#define LT_NUMBERS LT(_NUMBERS, KC_BSPC)

#define L_RGB_COUNT 5

uint16_t color_index = 0; 
uint8_t brightness = 100;
uint16_t colors[L_RGB_COUNT][2] = { 
    {131, 255},  // Light Blue
    {232, 255},    // Pink
    {0, 255},  // Red
    {43, 255},   // Yellow
    {170, 255}   // Blue
};

void update_underglow_color(void) {
    rgblight_sethsv(colors[color_index][0], colors[color_index][1], brightness);
}

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    switch (keycode) {
        case SCRN_BR_UP:
            if (record->event.pressed) {
                register_code(KC_LCTL);
                register_code(KC_LGUI);
                register_code(KC_LALT);
                register_code(KC_LSFT);
                register_code(KC_UP);
            } else {
                unregister_code(KC_UP);
                unregister_code(KC_LSFT);
                unregister_code(KC_LALT);
                unregister_code(KC_LGUI);
                unregister_code(KC_LCTL);
            }
            return false; // Prevent further processing

        case SCRN_BR_DN:
            if (record->event.pressed) {
                register_code(KC_LCTL);
                register_code(KC_LGUI);
                register_code(KC_LALT);
                register_code(KC_LSFT);
                register_code(KC_DOWN);
            } else {
                unregister_code(KC_DOWN);
                unregister_code(KC_LSFT);
                unregister_code(KC_LALT);
                unregister_code(KC_LGUI);
                unregister_code(KC_LCTL);
            }
            return false; // Prevent further processing
        
        case L_RGB_BR_UP:
            if (record->event.pressed) {
                brightness = brightness  + 10 <= 150 ? brightness + 10 : 150;
                update_underglow_color();  
            }
            return false;

        case L_RGB_BR_DN:
            if (record->event.pressed) {
                brightness = brightness - 10 >= 0 ? brightness - 10 : 0;
                update_underglow_color();
            }
            return false;

        case L_RGB_NXT:
            if (record->event.pressed) {
                color_index = (color_index + 1) % L_RGB_COUNT;
                update_underglow_color();
            }
            return false;
        
        case L_RGB_TGGL:
            if (record->event.pressed) {
                rgblight_toggle();
            }
            return false;
            
        case LT(0,KC_X):
            if (!record->tap.count && record->event.pressed) {
                tap_code16(C(KC_X)); // Intercept hold function to send Ctrl-X
                return false;
            }
            return true;             // Return true for normal processing of tap keycode

        case LT(0,KC_C):
            if (!record->tap.count && record->event.pressed) {
                tap_code16(C(KC_C)); // Intercept hold function to send Ctrl-C
                return false;
            }
            return true;             // Return true for normal processing of tap keycode

        case LT(0,KC_V):
            if (!record->tap.count && record->event.pressed) {
                tap_code16(C(KC_V)); // Intercept hold function to send Ctrl-V
                return false;
            }
            return true;             // Return true for normal processing of tap keycode
            
        case LT(0,KC_A):
            if (!record->tap.count && record->event.pressed) {
                tap_code16(C(KC_A)); // Intercept hold function to send Ctrl-A
                return false;
            }
            return true;
    }
    return true; // Process other keys as usual
}

void keyboard_post_init_user(void) {
    // Initialize RGB lighting here
    rgblight_enable();
    update_underglow_color();
}

